import React, {useState} from 'react';
import './styles.css';
import {Link, useHistory} from 'react-router-dom';
import {FiArrowLeft} from 'react-icons/fi';

import api from '../services/api';

import logoImg from '../../assets/logo.png';

export default function Register() {

    const [name,setName]=useState('');
    const [email,setEmail]=useState('');
    const [whatsapp,setWhatsapp]=useState('');
    const [city,setCity]=useState('');
    const [uf,setUF]=useState('');
    const [linkFoto,setlinkFoto]=useState('');
    const [centrolojistico,setCentroLogistico]=useState('');

    const history = useHistory();

    async function handleRegister(e) {
        e.preventDefault();
        const data = {name,email,whatsapp,city,uf, linkFoto,centrolojistico};

        try{
        const response =  await api.post('ongs', data);

        alert(`Seu ID de acesso: ${response.data.id} `);
        history.push('/');
        }catch(err){
            alert('Erro no cadastro, tente novamente.')
        }

    }

    return (
        <div className="register-container">
            <div className="content">
            <section>
            <img src={logoImg} alt="Cadastro Simples"/>

            <h1>Cadastro</h1>
            <p>Fa√ßa seu cadastro, entre na pataforma e ajude pessoas a encontrarem os casos da sua ONG.</p>
            <Link className="back-link" to  = "/"><FiArrowLeft size={16} color="#E02041"/>Tenho cadastro </Link> 
            </section>

            <form onSubmit={handleRegister}>
            <input placeholder="Nome da ONG" value={name}onChange={e=>setName(e.target.value)}></input>
            <input type="email" placeholder="E-mail" value={email}onChange={e=>setEmail(e.target.value)}></input>
            <input placeholder="Whatsapp" value={whatsapp}onChange={e=>setWhatsapp (e.target.value)}></input>
            <div className="input-group">
            <input placeholder="Cidade" value={city}onChange={e=>setCity(e.target.value)}></input>
            <input placeholder="UF" style={{width:80 }} value={uf}onChange={e=>setUF(e.target.value)}></input>
            </div>
            <input placeholder="instagram" value={linkFoto}onChange={e=>setlinkFoto (e.target.value)}></input>
            <input placeholder="CentroLogistico" value={centrolojistico}onChange={e=>setCentroLogistico (e.target.value)}></input>
            <button className="button" type="submit">Cadastrar</button>

            </form>

            </div>
        </div>
    )
}
